import { NEW_QUOTE } from '../actions'

const initState = {
    author: '',
    quote: ''
}

const newQuoteReducer = (state = initState, action) => {
    switch (action.type) {
        case NEW_QUOTE: {
            const { quote, author } = action.quote
            return { quote, author }
        }
        default: return state
    }
}
export default newQuoteReducer
