import React, { Component } from 'react';
import RandomQuote from './components/RandomQuote'
import {Provider,connect} from 'react-redux';
import {createStore} from 'redux';



const action=()=>{
  return {
    type: 'Click',
    quote:this.props.quote,
    author:this.props.author,
    color:this.props.color
  }
}
console.log(this.props.quote)
const defaultState={
    color:'red',
    quote:' When it became apparent that he wasn’t going to survive, Tyler’s mom talked to him about death. She comforted him by telling Tyler that she was dying too,',
    author:'red'
}
const reducer =(state = defaultState,action)=>{
  switch(action.type){
    case 'Click':
      return{
        color:action.color,
        author:action.author,
        quote:action.quote
      }
      default : return state;
  }
}
const store=createStore(reducer);
const mapStateToProps=(state =defaultState)=>{
  return defaultState;
}
const mapDispatchToProps =(dispatch)=>{
  return {
    onClick: () =>{
      dispatch(action());
    }
  }
}
const Contain=connect(mapStateToProps, mapDispatchToProps)(RandomQuote);
class App extends Component {
  constructor(props) {
    super(props)

    console.log('constructor runs')
  }

  componentDidMount() {
    console.log('componentDidMount runs')
  }

  render() {
    console.log('render method runs')
    return (
      <div>
        <Provider store={store}>
          <Contain/>
          </Provider>
      </div>
    );
  }
}
export default App;
