import { NEW_QUOTE } from '../actions'

const initState = {
    author: '',
    quote: '',
    color:''
}

const newQuoteReducer = (state = initState, action) => {
    switch (action.type) {
        case NEW_QUOTE: {
            return {
                author:action.quote.author,
                quote:action.quote.quote,
                color:action.quote.color
            }
        }
        default: return state
    }
}
export default newQuoteReducer
